mapper.select:
SELECT A.regist_id, A.student_id, DATE_FORMAT(A.reg_datetime, '%Y-%m-%d') reg_datetime, A.sports_id, A.steps, A.calorie, A.distance, B.alias sports_name, B.small_image_path, B.large_image_path, (SELECT regist_id FROM activity_registration WHERE student_id = #{userId} AND reg_datetime < A.reg_datetime ORDER BY reg_datetime DESC LIMIT 1) regist_id_prev, (SELECT regist_id FROM activity_registration WHERE student_id = #{userId} AND reg_datetime > A.reg_datetime ORDER BY reg_datetime ASC LIMIT 1) regist_id_next FROM activity_registration A JOIN activity_stports B ON A.sports_id = B.sports_id WHERE student_id = #{userId}
and A.regist_id = #{exerciseId}
ORDER BY reg_datetime DESC LIMIT 1

SELECT DATE_FORMAT(reg_datetime, '%m/%d') reg_datetime, B.alias sports_name, A.calorie, A.steps, A.distance FROM activity_registration A JOIN activity_stports B ON A.sports_id = B.sports_id WHERE A.student_id = #{userId}
AND A.reg_datetime <= (SELECT reg_datetime FROM activity_registration WHERE regist_id = #{exerciseId})
ORDER BY A.reg_datetime DESC LIMIT 4

SELECT A.regist_id, DATE_FORMAT(reg_datetime, '%Y.%m.%d') reg_datetime, B.alias sports_name, A.calorie, A.steps, A.distance, B.small_image_path FROM activity_registration A JOIN activity_stports B ON A.sports_id = B.sports_id WHERE A.student_id = #{userId}
AND A.reg_datetime < (SELECT reg_datetime FROM activity_registration WHERE regist_id = #{exerciseId})
ORDER BY A.reg_datetime DESC LIMIT 10

SELECT ROWNUM FROM (SELECT @RNUM:=@RNUM + 1 AS ROWNUM, O.* FROM (SELECT @RNUM:=0) R, (SELECT * FROM (SELECT A.regist_id, A.reg_datetime, A.student_id, A.sports_id, MAX(A.calorie) calorie, B.School_ID, B.Grade_ID, B.Class FROM activity_registration A JOIN school_register B ON A.student_id = B.Student_ID AND B.school_year = #{school_year} AND B.school_id = #{school_id}
AND B.grade_id = #{grade_id}
AND B.class = #{class}
WHERE A.reg_datetime LIKE CONCAT(#{reg_datetime},'%') AND A.sports_id = #{sports_id} GROUP BY A.student_id) D ORDER BY D.calorie DESC) O) Z WHERE student_id = #{userId}

SELECT 
	count(*) cnt,
    A.student_id,
    ROUND(AVG(A.calorie)) calorie,
    ROUND(AVG(A.steps)) steps,
    ROUND(AVG(A.distance), 1) distance
FROM
    activity_registration A
        JOIN
    school_register B ON A.student_id = B.Student_ID
        AND B.School_Year = #{school_year}
        
        AND B.School_ID = #{school_id}
        
        
        AND B.grade_id = #{grade_id}
        
        
        AND B.class = #{class}
        
WHERE
    A.reg_datetime LIKE CONCAT(#{reg_datetime},'%')
        AND A.sports_id = #{sports_id}
        AND A.bmi_status = #{bmi_status}
