mapper.select:
SELECT * FROM ( ( SELECT '1' AS isOwn, A.school_id, A.school_name, A.section, A.qw, B.menu_id, B.school_year, B.school_month, B.school_date, B.school_day, ( CASE B.lunch_main WHEN '' THEN '&nbsp;' ELSE B.lunch_main END ) AS lunch_main, ( CASE B.lunch_detl WHEN '' THEN '&nbsp;' ELSE B.lunch_detl END ) AS lunch_detl , ( CASE B.lunch_calorie WHEN '' THEN '&nbsp;' ELSE B.lunch_calorie END ) AS lunch_calorie , ( CASE B.dinner_main WHEN '' THEN '&nbsp;' ELSE B.dinner_main END ) AS dinner_main , ( CASE B.dinner_detl WHEN '' THEN '&nbsp;' ELSE B.dinner_detl END ) AS dinner_detl , ( CASE B.dinner_calorie WHEN '' THEN '&nbsp;' ELSE B.dinner_calorie END ) AS dinner_calorie, B.carbohydrate, B.protein, B.fat, B.calcium, B.vitamin_A, B.vitamin_C, B.nutrient_etc, B.img_url, B.use_yn FROM ( SELECT * FROM (SELECT * FROM SCHOOL_INFO WHERE SECTION = #{section}) SI, (SELECT #{date-4} AS QW FROM DUAL UNION ALL SELECT #{date-3} AS QW FROM DUAL UNION ALL SELECT #{date-2} AS QW FROM DUAL UNION ALL SELECT #{date-1} AS QW FROM DUAL UNION ALL SELECT #{date-0} AS QW FROM DUAL) TMPDATE ) A LEFT JOIN MENU_INFO AS B ON A.QW = CONCAT(B.SCHOOL_YEAR, B.SCHOOL_MONTH, B.SCHOOL_DATE) AND A.SCHOOL_ID = B.SCHOOL_ID WHERE A.SCHOOL_ID = #{school_id} )
UNION ALL
( SELECT '2' AS isOwn, A.school_id, A.school_name, A.section, A.qw, B.menu_id, B.school_year, B.school_month, B.school_date, B.school_day, ( CASE B.lunch_main WHEN '' THEN '&nbsp;' ELSE B.lunch_main END ) AS lunch_main, ( CASE B.lunch_detl WHEN '' THEN '&nbsp;' ELSE B.lunch_detl END ) AS lunch_detl , ( CASE B.lunch_calorie WHEN '' THEN '&nbsp;' ELSE B.lunch_calorie END ) AS lunch_calorie , ( CASE B.dinner_main WHEN '' THEN '&nbsp;' ELSE B.dinner_main END ) AS dinner_main , ( CASE B.dinner_detl WHEN '' THEN '&nbsp;' ELSE B.dinner_detl END ) AS dinner_detl , ( CASE B.dinner_calorie WHEN '' THEN '&nbsp;' ELSE B.dinner_calorie END ) AS dinner_calorie, B.carbohydrate, B.protein, B.fat, B.calcium, B.vitamin_A, B.vitamin_C, B.nutrient_etc, B.img_url, B.use_yn FROM ( SELECT * FROM (SELECT * FROM SCHOOL_INFO WHERE SECTION = #{section}) SI, (SELECT #{date-4} AS QW FROM DUAL UNION ALL SELECT #{date-3} AS QW FROM DUAL UNION ALL SELECT #{date-2} AS QW FROM DUAL UNION ALL SELECT #{date-1} AS QW FROM DUAL UNION ALL SELECT #{date-0} AS QW FROM DUAL) TMPDATE ) A LEFT JOIN MENU_INFO AS B ON A.QW = CONCAT(B.SCHOOL_YEAR, B.SCHOOL_MONTH, B.SCHOOL_DATE) AND A.SCHOOL_ID = B.SCHOOL_ID WHERE A.SCHOOL_ID NOT IN (#{school_id}, '1', '2','5','6','19') ) ) C ORDER BY C.isOwn ASC, C.school_id asc, C.qw desc ;

mapper.update:
UPDATE student_allergy_set_input
SET
   STU_ALG_SET_IN_STATE_TEMP = #{alg_state} 
   ,STU_ALG_SET_IN_UPDATE = NOW()
WHERE 
    student_id = #{userId}
        and ALG_FOOD_ID = #{alg_id}

UPDATE student_breakfast_set_input
SET
   BKST_EAT_CATE_ID = #{bkst_id} 
   ,STU_BKST_SET_IN_UPDATE = NOW()
WHERE 
    student_id = #{userId}
